import Navbar from "../components/Navbar";
import Cta from "../components/CTA";
import Footer from "../components/Footer";
import { InformationCircleIcon } from "@heroicons/react/outline";


const info = [
  {
    title: "The Fine Details",
    text: "This is info text about the slide",
    ImageURL:
      "https://tailwindui.com/img/ecommerce-images/product-page-04-detail-product-shot-01.jpg",
  },
  {
    title: "The next Details",
    text: "Ooooh new info about the next slide",
    ImageURL:
      "https://tailwindui.com/img/ecommerce-images/product-page-04-detail-product-shot-02.jpg",
  },
];

export default function Design() {
  return (
    <div className="bg-gray-50">
      <Navbar />
      <div className="max-w-2xl mx-auto px-4 py-24 sm:px-6 sm:py-32 lg:max-w-7xl lg:px-8">
        <section aria-labelledby="details-heading">
          <div className="flex flex-col items-center text-center">
            <h2
              id="details-heading"
              className="text-3xl font-extrabold tracking-tight text-gray-900 sm:text-4xl"
            >
              The Fine Details
            </h2>
            <p className="mt-3 max-w-3xl text-lg text-gray-600">
              Our patented padded snack sleeve construction protects your
              favorite treats from getting smooshed during all-day adventures,
              long shifts at work, and tough travel schedules.
            </p>
          </div>

          <div className="mt-16 grid grid-cols-1 gap-y-16 lg:grid-cols-2 lg:gap-x-8">
            { info.map((data) => {
                <div>
                  <div className="w-full aspect-w-3 aspect-h-2 rounded-lg overflow-hidden">
                    <img
                      src={data.ImageURL}
                      alt="Drawstring top with elastic loop closure and textured interior padding."
                      className="w-full h-full object-center object-cover"
                    />
                  </div>
                  <p className="mt-8 text-lg text-gray-500">{data.title}</p>
                  <p className="mt-8 text-base text-gray-500">{data.text}</p>
                </div>;
            })}
          </div>
        </section>
      </div>
      <Cta />
      <Footer />
    </div>
  );
}
